import { creactSelector } from 'reselect';

// we should use reselect to avoid rerendering of the components,
// if the state object is not changed
const selectCart = state => state.cart;

export const selectCartItems = creactSelector(
  [selectCart],
  (cart) => cart.cartItems
)

export const selectCartItemsCount = creactSelector(
  [selectCartItems],
  cartItems =>
  cartItems.reduce(
    (accumalateQuantity, cartItem) => accumalateQuantity + cartItem.quantity, 
    0
  )
)
